package com.carlosmonjaraz.devmon.viewpagertablayoutexample

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import com.bumptech.glide.Glide
import com.carlosmonjaraz.devmon.viewpagertablayoutexample.databinding.FragmentTypeBinding

class TeamsFragment : Fragment() {

    private var _binding: FragmentTypeBinding? = null
    private val binding get() = _binding!!

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        _binding = FragmentTypeBinding.inflate(inflater, container, false)
        val root: View = binding.root

        initUI()
        return root
    }

    private fun initUI() {
        Glide.with(requireContext()).load("https://thumbs.dreamstime.com/b/logo-de-la-federaci%C3%B3n-f%C3%BAtbol-portugal-con-bandera-nacional-fifa-mundial-copa-ilustraci%C3%B3n-245990038.jpg").into(binding.incType.imgType)
        binding.incType.txtName.text = getString(R.string.selection)

        Glide.with(requireContext()).load("https://i.pinimg.com/originals/c1/5e/6d/c15e6d719debe4b3ed589c06045655dc.png").into(binding.incD1.imgType)
        binding.incD1.txtName.text = getString(R.string.eq1)

        Glide.with(requireContext()).load("https://1000marcas.net/wp-content/uploads/2020/01/Juventus-Logo-2017.jpg").into(binding.incD2.imgType)
        binding.incD2.txtName.text = getString(R.string.eq2)

        Glide.with(requireContext()).load("data:image/png;base64,").into(binding.incD3.imgType)
        binding.incD3.txtName.text = getString(R.string.eq3)
    }
}